        case "switchOn/Off":
            if (state.isSwitchedOn === false) {
                state = { ...state, speed: 0 };
            }
            return (state = { ...state, isSwitchedOn: !state.isSwitchedOn });
        case "accelerate":
            return (state = { ...state, speed: state.speed + 5 });
            case "brake":
              if(state.speed>=5){ 
              return (state = { ...state, speed: state.speed - 5 });
            }else {
            //   return(state ={
            //   ...state,speed:0
            // })
            }


            case "machtNull":
              return(state ={
                ...state,speed:0
              })

        default:
    }
};
function App() {
    const [state, dispatch] = useReducer(reducer, initState);
    return (
        <div className="App">
            <Car state={state} dispatch={dispatch} />
        </div>
    );
}

export default App;
